CREATE TABLE Cargo(idCargo INTEGER PRIMARY KEY, nombre VARCHAR2, fechaInicio INTEGER, fechaFin INTEGER);
CREATE TABLE Territorio(idTerritorio INTEGER PRIMARY KEY, nombre VARCHAR2, idSupraTerritorio INTEGER, FOREIGN KEY(idSupraTerritorio) REFERENCES Territorio(idTerritorio));
CREATE TABLE Votante(DNI INTEGER PRIMARY KEY, nombre VARCHAR2, idTerritorio INTEGER, FOREIGN KEY(idTerritorio) REFERENCES Territorio(idTerritorio));
CREATE TABLE Camioneta(Patente VARCHAR2 PRIMARY KEY, DNI INTEGER, FOREIGN KEY(DNI) REFERENCES Votante(DNI));
CREATE TABLE Centro(idCentro INTEGER PRIMARY KEY, direccion VARCHAR2, patente VARCHAR2, idTerritorio INTEGER, FOREIGN KEY(patente) REFERENCES Camioneta(patente), FOREIGN KEY(idTerritorio) REFERENCES Territorio(idTerritorio));
CREATE TABLE Mesa(idMesa INTEGER PRIMARY KEY, idCentro INTEGER, FOREIGN KEY(idCentro) REFERENCES Centro(idCentro));
CREATE TABLE Contiene(idEleccion INTEGER, idMesa INTEGER, DNI_Presidente INTEGER, DNI_Vice INTEGER, DNI_Tecnico INTEGER, PRIMARY KEY(idEleccion,idMesa), FOREIGN KEY(idEleccion) REFERENCES VotacionEleccion(idEleccion), FOREIGN KEY(idMesa) REFERENCES Mesa(idMesa), FOREIGN KEY(DNI_Presidente) REFERENCES Votante(DNI), FOREIGN KEY(DNI_Vice) REFERENCES Votante(DNI), FOREIGN KEY(DNI_Tecnico) REFERENCES Votante(DNI));
CREATE TABLE EsFiscal(DNI INTEGER, idEleccion INTEGER, idMesa INTEGER, idPartido INTEGER, PRIMARY KEY(DNI,idEleccion,idMesa), FOREIGN KEY(idEleccion) REFERENCES VotacionEleccion(idEleccion), FOREIGN KEY(idMesa) REFERENCES Mesa(idMesa), FOREIGN KEY(DNI) REFERENCES Votante(DNI), FOREIGN KEY(idPartido) REFERENCES PartidoPolitico(idPartido));
CREATE TABLE EsCandidato(DNI INTEGER, idEleccion INTEGER, idPartido INTEGER, cantVotos INTEGER, PRIMARY KEY(DNI,idEleccion), FOREIGN KEY(idEleccion) REFERENCES VotacionEleccion(idEleccion), FOREIGN KEY(DNI) REFERENCES Votante(DNI), FOREIGN KEY(idPartido) REFERENCES PartidoPolitico(idPartido));
CREATE TABLE CargoParaTerritorio(idCargo INTEGER, idTerritorio INTEGER, PRIMARY KEY(idCargo,idTerritorio), FOREIGN KEY(idCargo) REFERENCES Cargo(idCargo), FOREIGN KEY(idTerritorio) REFERENCES Territorio(idTerritorio));
CREATE TABLE VotacionEleccion(idEleccion INTEGER PRIMARY KEY, fecha INTEGER);
CREATE TABLE VotacionCandidato(idEleccion INTEGER PRIMARY KEY, idCargo INTEGER, idTerritorio INTEGER, FOREIGN KEY(idEleccion) REFERENCES VotacionEleccion(idEleccion), FOREIGN KEY(idCargo) REFERENCES Cargo(idCargo), FOREIGN KEY(idTerritorio) REFERENCES Territorio(idTerritorio));
CREATE TABLE Vota(DNI INTEGER, idEleccion INTEGER, idMesa INTEGER, hora INTEGER, PRIMARY KEY(DNI,idEleccion,idMesa), FOREIGN KEY(DNI) REFERENCES Votante(DNI), FOREIGN KEY(idEleccion) REFERENCES VotacionEleccion(idEleccion), FOREIGN KEY(idMesa) REFERENCES Mesa(idMesa));
CREATE TABLE PartidoPolitico(idPartido INTEGER PRIMARY KEY, nombre VARCHAR2);